document.addEventListener("DOMContentLoaded",function(){let t=new class{constructor(t){this.$$star=Array.prototype.slice.call(document.querySelectorAll(".star svg")),this.$starContainer=document.querySelector(".star-container")}start(){this.$starContainer.addEventListener("mouseout",this.onMouseOut.bind(this)),this.$$star.forEach((t,e)=>{t.addEventListener("mouseenter",this.onMouseEnter.bind(this)),t.addEventListener("click",this.onClick.bind(this))})}onMouseEnter(t){let e=parseInt(t.currentTarget.parentElement.querySelector(".star-radio").value);this.$$star.forEach(t=>{let r=parseInt(t.parentElement.querySelector(".star-radio").value);r<=e?t.classList.add("is-shining"):t.classList.remove("is-shining")})}onMouseOut(t){let e=t.clientX,r=t.clientY,n=t.currentTarget.getBoundingClientRect();(e<n.left||e>n.right||r<n.top||r>n.bottom)&&this.$$star.forEach(t=>{t.classList.remove("is-shining")})}onClick(t){let e=parseInt(t.currentTarget.parentElement.querySelector(".star-radio").value);this.$starContainer.dataset.rating=e}};t.start()});